INSERT INTO "movies" VALUES(4814,'Sex & the city','Sex and the City - The Movie','allotracker','','','Quatre ans ont passé... Carrie, Samantha, Charlotte et Miranda vivent toujours à New York et sont restées d''inséparables amies. Même si leurs folles années sont derrière elles, les quatre copines de "Sex & the City" continuent de parler haut et fort de ce qui les tracasse : les relations amoureuses. Carrière, amour, réussite, maternité, sexe ou encore engagement... toutes ont vécu une évolution capitale. Carrie vit enfin sa love story aux côtés de Mr Big, Charlotte élève sa fille avec son cher Harry, Miranda jongle entre Brady, Steve et sa carrière. Quant à Samantha, elle est toujours en couple avec le beau Smith. Mais, à New York, c''est toujours lorsque tout va bien qu''un orage éclate...','http://www.imdb.com/title/tt1000774/','','5.4','1080p','en','fr','Sarah Jessica Parker, Kim Cattrall and Cynthia Nixon','Michael Patrick King','USA','','mkv',4,'','','','','2011-01-06 11:54:42.033340','2011-06-15 16:14:11.648673',NULL,NULL,NULL,NULL,'18938386_w434_h_q80.jpeg','image/jpeg',50271,'2011-01-06 11:54:41.730870',2008,NULL,'f',NULL,'f',NULL,NULL,NULL,NULL,12);
